interface Props{
 focus:boolean;
 onFocusToggle:()=>void;
 onContrast:()=>void;
 spacing:string;
 setSpacing:(v:string)=>void;
 fontSize:string;
 setFontSize:(v:string)=>void;
 onProfile:()=>void;
 tea:boolean;

}

export default function Dashboard(props:Props){
  return(
    <header className="dashboard">
      <div className="title-area">
        <h1 className="logo">MindEase</h1>
        <div className="profile-btn">
          Perfil
          <div className="profile-menu">
            <div className="profile-item" data-profile="normal">Perfil Normal</div>
            <div className="profile-item" data-profile="tea">Perfil TEA</div>
            <div className="profile-item" data-profile="dyslexia">Perfil Dislexia</div>
          </div>
        </div>
      </div>
      <p className="subtitle">Experiência cognitiva leve e guiada</p>
      <div className="controls">
        <button className="btn primary" onClick={props.onFocusToggle}>
          {props.focus?'Desativar Foco':'Ativar Foco'}
        </button>
        <button className="btn warn" onClick={props.onContrast}>Alternar Contraste</button>

        <select className="styled-select" value={props.spacing} onChange={e=>props.setSpacing(e.target.value)}>
          <option value="compact">Compacto</option>
          <option value="normal">Normal</option>
          <option value="relaxed">Espaçado</option>
        </select>

        <select className="styled-select" value={props.fontSize} onChange={e=>props.setFontSize(e.target.value)}>
          <option value="small">Pequena</option>
          <option value="normal">Normal</option>
          <option value="large">Grande</option>
        </select>

        <button className="btn secondary" onClick={props.onTea}>
          {props.tea?'Desativar Tema TEA':'Tema TEA'}
        </button>

        <button className="btn secondary" onClick={props.onDyslexia}>{props.tea?'Desativar Tema TEA':'Tema TEA'}</button>

        <button className="btn secondary" onClick={props.onProfile}>{props.dyslexia?'Desativar Dislexia':'Tema Dislexia'}</button>
      </div>
    </header>
  );
}
