import { useEffect,useState } from 'react';

export default function Profile(){
 const [data,setData]=useState<any>({ needs:'', routine:'',nav:'guided'});

 useEffect(()=>{
  const saved=localStorage.getItem('profile');
  if(saved) setData(JSON.parse(saved));
 },[]);

 function save(){
  localStorage.setItem('profile',JSON.stringify(data));
  alert('Perfil salvo');
 }

 return (
  <section className="profile">
    <h2>Perfil Cognitivo</h2>
    <textarea
      placeholder="Necessidades específicas"
      value={data.needs}
      onChange={e=>setData({...data,needs:e.target.value})}/>

    <textarea
      placeholder="Rotina de estudo/trabalho"
      value={data.routine}
      onChange={e=>setData({...data,routine:e.target.value})}/>

    <select value={data.nav} onChange={e=>setData({...data,nav:e.target.value})}>
      <option value="guided">Navegação guiada</option>
      <option value="free">Navegação livre</option>
    </select>

    <button onClick={save}>Salvar</button>
  </section>
 );
}
