import { useEffect, useState } from 'react';
import Dashboard from './components/Dashboard';
import Kanban from './components/Kanban';

export default function App() {
  const [focusMode, setFocusMode] = useState(false);
  const [contrast, setContrast] = useState('normal');
  const [spacing, setSpacing] = useState('normal');
  const [fontSize, setFontSize] = useState('normal');
  const [summary, setSummary] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem('prefs');
    if(saved) {
      const p = JSON.parse(saved);
      setFocusMode(p.focusMode);
      setContrast(p.contrast);
      setSpacing(p.spacing);
      setFontSize(p.fontSize);
      setSummary(p.summary);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('prefs', JSON.stringify({focusMode, contrast, spacing, fontSize, summary}));
  }, [focusMode, contrast, spacing, fontSize, summary]);

  return (
    <div className={`app ${focusMode ? 'focus' : ''} ${contrast === 'high' ? 'contrast' : ''} ${spacing} ${fontSize}`}>
      <Dashboard 
        focus={focusMode} 
        onFocusToggle={() => setFocusMode(!focusMode)}
        onContrast={() => setContrast(contrast === 'normal' ? 'high' : 'normal')}
        spacing={spacing}
        setSpacing={setSpacing}
        fontSize={fontSize}
        setFontSize={setFontSize}
        summary={summary}
        setSummary={setSummary}
      />
      <Kanban summary={summary} />
    </div>
  );
}
