import { useEffect,useState } from 'react';

export default function Pomodoro(){
 const [seconds,setSeconds]=useState(1500); // 25m
 const [running,setRunning]=useState(false);
 const [phase,setPhase]=useState<'focus'|'break'>('focus');

 useEffect(()=>{
  if(!running) return;
  const t=setInterval(()=>setSeconds(s=>s-1),1000);
  return ()=>clearInterval(t);
 },[running]);

 useEffect(()=>{
  if(seconds<=0){
    if(phase==='focus'){ setPhase('break'); setSeconds(300);} // 5m
    else{ setPhase('focus'); setSeconds(1500);}
  }
 },[seconds,phase]);

 const mins=Math.floor(seconds/60).toString().padStart(2,'0');
 const secs=(seconds%60).toString().padStart(2,'0');

 return (
  <div className="pomodoro">
    <h3>{phase==='focus'?'Foco':'Pausa'}</h3>
    <div className="timer">{mins}:{secs}</div>
    <button onClick={()=>setRunning(!running)}>{running?'Pausar':'Iniciar'}</button>
    <button onClick={()=>{setRunning(false);setPhase('focus');setSeconds(1500);}}>Resetar</button>
  </div>
 );
}
