import { useEffect, useState } from 'react';

type Tasks = {
  todo: string[];
  doing: string[];
  done: string[];
};

export default function Kanban({ summary }: { summary: boolean }) {
  const [newTask, setNewTask] = useState('');
  const [alert, setAlert] = useState('');
  const [tasks, setTasks] = useState<Tasks>({
    todo: ['Planejar semana'],
    doing: ['Estudar React'],
    done: ['Setup inicial']
  });

  useEffect(() => {
    const timer = setTimeout(
      () => setAlert('Você está há um tempo nesta tarefa — pausa rápida?'),
      5000
    );
    return () => clearTimeout(timer);
  }, []);

  function addTask() {
    if (!newTask.trim()) return;
    setTasks({
      ...tasks,
      todo: [...tasks.todo, newTask]
    });
    setNewTask('');
  }

  function onDragStart(e: React.DragEvent<HTMLDivElement>, column: keyof Tasks, index: number) {
    e.dataTransfer.setData(
      'source',
      JSON.stringify({ column, index })
    );
  }

  function onDrop(e: React.DragEvent<HTMLDivElement>, target: keyof Tasks) {
    const data = JSON.parse(e.dataTransfer.getData('source'));
    if (!data) return;

    const moved = tasks[data.column][data.index];

    const newData: Tasks = {
      todo: [...tasks.todo],
      doing: [...tasks.doing],
      done: [...tasks.done]
    };

    newData[data.column].splice(data.index, 1);
    newData[target].push(moved);

    setTasks(newData);
  }

  return (
    <section className="kanban">
      {alert && <div className="alert">{alert}</div>}

      <div className="add-area">
        <input
          value={newTask}
          onChange={e => setNewTask(e.target.value)}
          placeholder="Digite uma tarefa..."
        />
        <button onClick={addTask}>Adicionar</button>
      </div>

      {Object.entries(tasks).map(([key, list]) => {
        const typedKey = key as keyof Tasks;

        return (
          <div
            className="column"
            key={key}
            onDragOver={(e) => e.preventDefault()}
            onDrop={(e) => onDrop(e, typedKey)}
          >
            <h3>
              {key === 'todo'
                ? 'A Fazer'
                : key === 'doing'
                ? 'Em Progresso'
                : 'Concluído'}
            </h3>

            {list.map((item, i) => (
              <div
                className={`card ${
                  key === 'doing' ? 'pulse' : ''
                } ${key === 'done' ? 'done' : ''}`}
                key={i}
                draggable
                onDragStart={(e) => onDragStart(e, typedKey, i)}
                contentEditable={!summary}
                suppressContentEditableWarning
              >
                {item}
              </div>
            ))}
          </div>
        );
      })}
    </section>
  );
}
