import { useEffect, useRef, useState } from 'react';

type Tasks = {
  todo: string[];
  doing: string[];
  done: string[];
};

export default function Kanban() {
  const [newTask, setNewTask] = useState('');
  const [showAlert, setShowAlert] = useState(false);

  const [confirmDelete, setConfirmDelete] = useState(false);
  const [taskToDelete, setTaskToDelete] = useState<{column: keyof Tasks, index: number} | null>(null);

  const [tasks, setTasks] = useState<Tasks>({
    todo: [],
    doing: [],
    done: []
  });

  const timerRef = useRef<any>(null);

  function startInactivityTimer() {
    clearTimeout(timerRef.current);
    timerRef.current = setTimeout(() => {
      const focused = document.activeElement as HTMLInputElement | null;

      if (focused && focused.tagName === 'INPUT' && focused.value.trim() === '') {
        setShowAlert(true);
      }
    }, 5000);
  }

  function resetAlertAndRestart() {
    setShowAlert(false);
    startInactivityTimer();
  }

  // üî• Listener global para QUALQUER INPUT
  useEffect(() => {
    function handleFocus(e: any) {
      if (e.target.tagName === 'INPUT') startInactivityTimer();
    }

    function handleInput(e: any) {
      if (e.target.tagName === 'INPUT') startInactivityTimer();
    }

    window.addEventListener('focusin', handleFocus);
    window.addEventListener('input', handleInput);

    return () => {
      window.removeEventListener('focusin', handleFocus);
      window.removeEventListener('input', handleInput);
      clearTimeout(timerRef.current);
    };
  }, []);

  function addTask() {
    if (!newTask.trim()) return;
    setTasks({
      ...tasks,
      todo: [...tasks.todo, newTask]
    });
    setNewTask('');
  }

  function onDragStart(e: React.DragEvent<HTMLDivElement>, column: keyof Tasks, index: number) {
    e.dataTransfer.setData('source', JSON.stringify({ column, index }));
  }

  function onDrop(e: React.DragEvent<HTMLDivElement>, target: keyof Tasks) {
    const data = JSON.parse(e.dataTransfer.getData('source'));
    if (!data) return;

    const moved = tasks[data.column][data.index];

    const newData: Tasks = {
      todo: [...tasks.todo],
      doing: [...tasks.doing],
      done: [...tasks.done]
    };

    newData[data.column].splice(data.index, 1);
    newData[target].push(moved);

    setTasks(newData);
  }

  function confirmDeleteTask(column: keyof Tasks, index: number) {
    setTaskToDelete({column, index});
    setConfirmDelete(true);
  }

  function deleteTask() {
    if(!taskToDelete) return;

    const newData: Tasks = {
      todo: [...tasks.todo],
      doing: [...tasks.doing],
      done: [...tasks.done]
    };

    newData[taskToDelete.column].splice(taskToDelete.index,1);
    setTasks(newData);
    setConfirmDelete(false);
    setTaskToDelete(null);
  }

  return (
    <section className="kanban">

      {showAlert && (
        <div className="modal-overlay" onClick={resetAlertAndRestart}>
          <div className="modal" onClick={(e)=>e.stopPropagation()}>
            <h2>‚ö†Ô∏è Pausa Cognitiva</h2>
            <p>Voc√™ est√° h√° bastante tempo parado nesta a√ß√£o. Que tal respirar um pouco?</p>
            <button className="btn primary" onClick={resetAlertAndRestart}>
              Continuar
            </button>
          </div>
        </div>
      )}

      {confirmDelete && (
        <div className="modal-overlay" onClick={()=>setConfirmDelete(false)}>
          <div className="modal" onClick={(e)=>e.stopPropagation()}>
            <h2>Excluir tarefa?</h2>
            <p>Voc√™ tem certeza que quer excluir essa tarefa?</p>

            <div style={{display:'flex', gap:'12px', justifyContent:'center'}}>
              <button className="btn warn" onClick={deleteTask}>Sim</button>
              <button className="btn secondary" onClick={()=>setConfirmDelete(false)}>N√£o</button>
            </div>
          </div>
        </div>
      )}

      <div className="add-area">
        <input
          value={newTask}
          onChange={e => setNewTask(e.target.value)}
          placeholder="Digite uma tarefa..."
          className="task-input"
        />
        <button className="btn add" onClick={addTask}>Adicionar</button>
      </div>

      {Object.entries(tasks).map(([key, list]) => {
        const typedKey = key as keyof Tasks;

        return (
          <div
            className="column"
            key={key}
            onDragOver={(e) => e.preventDefault()}
            onDrop={(e) => onDrop(e, typedKey)}
          >
            <h3>
              {key === 'todo'
                ? 'A Fazer'
                : key === 'doing'
                ? 'Em Progresso'
                : 'Conclu√≠do'}
            </h3>

            {list.map((item, i) => (
              <div
                className={`card ${key === 'doing' ? 'pulse' : ''} ${key === 'done' ? 'done' : ''}`}
                key={i}
                draggable
                onDragStart={(e) => onDragStart(e, typedKey, i)}
              >
                <input
                  defaultValue={item}
                  className="task-inline-input"
                />
                <span className="trash" onClick={()=>confirmDeleteTask(typedKey,i)}>üóëÔ∏è</span>
              </div>
            ))}
          </div>
        );
      })}
    </section>
  );
}
