import { useState } from 'react';
import type { Task } from '../domain/task';

export default function Kanban(){
 const [tasks,setTasks]=useState<Task[]>([]);

 function addTask(){
  const title=prompt('Nome da tarefa?');
  if(!title) return;
  setTasks([...tasks,{id:crypto.randomUUID(),title,status:'todo',checklist:[]}]);
 }

 function move(id:string, status:'todo'|'doing'|'done'){
  setTasks(tasks.map(t=>t.id===id?{...t,status}:t));
 }

 function addChecklist(id:string){
  const text=prompt('Novo passo?');
  if(!text) return;
  setTasks(tasks.map(t=> t.id===id?{...t,checklist:[...t.checklist,{id:crypto.randomUUID(),text,done:false}]}:t));
 }

 function toggleChecklist(taskId:string,itemId:string){
  setTasks(tasks.map(t=> t.id!==taskId?t:{
   ...t,
   checklist:t.checklist.map(c=>c.id===itemId?{...c,done:!c.done}:c)
  }));
 }

 return (
  <div className="kanban">
    <button onClick={addTask}>Adicionar tarefa</button>
    {['todo','doing','done'].map(col=>(
      <div key={col} className="column">
        <h3>{col}</h3>
        {tasks.filter(t=>t.status===col).map(t=>(
          <div key={t.id} className="card">
            <strong>{t.title}</strong>
            {col!=='done' && <>
              <button onClick={()=>move(t.id,'doing')}>Fazer</button>
              <button onClick={()=>move(t.id,'done')}>Concluir</button>
              <button onClick={()=>addChecklist(t.id)}>+Checklist</button>
            </>}
            <ul>
              {t.checklist.map(c=>(
                <li key={c.id}>
                  <label>
                    <input type="checkbox" checked={c.done} onChange={()=>toggleChecklist(t.id,c.id)}/>
                    {c.text}
                  </label>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    ))}
  </div>
 );
}
