interface Props {
  focus: boolean;
  onFocusToggle: () => void;
  onContrast: () => void;
  spacing: string;
  setSpacing: (v: string) => void;
  fontSize: string;
  setFontSize: (v: string) => void;
}

export default function Dashboard({focus,onFocusToggle,onContrast,spacing,setSpacing,fontSize,setFontSize}:Props) {
  return (
    <header className="dashboard">
      <h1 className="logo">MindEase</h1>
      <p className="subtitle">Experiência cognitiva leve e guiada</p>

      <div className="controls">
        <button className="btn primary" onClick={onFocusToggle}>
          {focus ? 'Desativar Foco' : 'Ativar Foco'}
        </button>

        <button className="btn warn" onClick={onContrast}>
          Alternar Contraste
        </button>

        <select onChange={(e)=>setSpacing(e.target.value)} value={spacing}>
          <option value="compact">Compacto</option>
          <option value="normal">Normal</option>
          <option value="relaxed">Espaçado</option>
        </select>

        <select onChange={(e)=>setFontSize(e.target.value)} value={fontSize}>
          <option value="small">Pequena</option>
          <option value="normal">Normal</option>
          <option value="large">Grande</option>
        </select>
      </div>
    </header>
  );
}
