import { useEffect, useRef, useState } from 'react';

type Checklist = { id:string; text:string; done:boolean };

type Tasks = {
  todo: { text:string; checklist:Checklist[] }[];
  doing: { text:string; checklist:Checklist[] }[];
  done: { text:string; checklist:Checklist[] }[];
};

export default function Kanban() {
  const [newTask, setNewTask] = useState('');
  const [inputError, setInputError] = useState(false);
  const [showAlert, setShowAlert] = useState(false);

  const [confirmDelete, setConfirmDelete] = useState(false);
  const [taskToDelete, setTaskToDelete] = useState<{column: keyof Tasks, index: number} | null>(null);

  const [showChecklistInput,setShowChecklistInput]=useState<{column:keyof Tasks,index:number}|null>(null);

  const [confirmDone, setConfirmDone] = useState(false);
  const [pendingDoneTask, setPendingDoneTask] = useState<{column: keyof Tasks, index: number} | null>(null);

  const [tasks, setTasks] = useState<Tasks>({
    todo: [],
    doing: [],
    done: []
  });

  const timerRef = useRef<any>(null);

  function startInactivityTimer() {
    clearTimeout(timerRef.current);
    timerRef.current = setTimeout(() => {
      const focused = document.activeElement as HTMLInputElement | null;
      if (focused && focused.tagName === 'INPUT') {
        setShowAlert(true);
      }
    }, 15000);
  }

  function resetAlertAndRestart() {
    setShowAlert(false);
    startInactivityTimer();
  }

  useEffect(() => {
    function handleFocus(e: any) {
      if (e.target.tagName === 'INPUT') startInactivityTimer();
    }
    function handleInput(e: any) {
      if (e.target.tagName === 'INPUT') startInactivityTimer();
    }

    window.addEventListener('focusin', handleFocus);
    window.addEventListener('input', handleInput);

    return () => {
      window.removeEventListener('focusin', handleFocus);
      window.removeEventListener('input', handleInput);
      clearTimeout(timerRef.current);
    };
  }, []);

  function addTask() {
    if (!newTask.trim()) {
      setInputError(true);
      return;
    }

    setTasks({
      ...tasks,
      todo: [...tasks.todo, { text:newTask, checklist:[] }]
    });

    setNewTask('');
    setInputError(false);
  }

  function confirmDeleteTask(column: keyof Tasks, index: number) {
    setTaskToDelete({column, index});
    setConfirmDelete(true);
  }

  function deleteTask() {
    if(!taskToDelete) return;

    const newData: Tasks = JSON.parse(JSON.stringify(tasks));
    newData[taskToDelete.column].splice(taskToDelete.index,1);
    setTasks(newData);
    setConfirmDelete(false);
    setTaskToDelete(null);
  }

  function onDragStart(e: React.DragEvent<HTMLDivElement>, column: keyof Tasks, index: number) {
    if (column === 'done') return;
    e.dataTransfer.setData('source', JSON.stringify({ column, index }));
  }

  function onDrop(e: React.DragEvent<HTMLDivElement>, target: keyof Tasks) {
    const data = JSON.parse(e.dataTransfer.getData('source'));
    if (!data) return;

    if (target === 'done') {
      setPendingDoneTask(data);
      setConfirmDone(true);
      return;
    }

    moveTask(data.column, data.index, target);
  }

  function moveTask(from: keyof Tasks, index: number, to: keyof Tasks) {
    const newData: Tasks = JSON.parse(JSON.stringify(tasks));
    const moved = newData[from][index];
    newData[from].splice(index,1);
    newData[to].push(moved);
    setTasks(newData);
  }

  function confirmDoneMove() {
    if(!pendingDoneTask) return;
    moveTask(pendingDoneTask.column, pendingDoneTask.index, 'done');
    setConfirmDone(false);
    setPendingDoneTask(null);
  }


  function addChecklist(column:keyof Tasks,index:number){
    setShowChecklistInput({column,index});
  }

  function saveChecklist(column:keyof Tasks,index:number,text:string){
    const newData:Tasks=JSON.parse(JSON.stringify(tasks));
    newData[column][index].checklist.push({id:crypto.randomUUID(),text,done:false});
    setTasks(newData);
    setShowChecklistInput(null);
  }

  function checklistProgress(list:Checklist[]){
    if(!list.length) return 0;
    const done=list.filter(i=>i.done).length;
    return Math.round((done/list.length)*100);
  }

  function toggleChecklist(column:keyof Tasks,taskIndex:number,itemId:string){
    const newData:Tasks=JSON.parse(JSON.stringify(tasks));
    const list=newData[column][taskIndex].checklist;
    const item=list.find(i=>i.id===itemId);
    if(item) item.done=!item.done;
    setTasks(newData);
  }

  return (
    <section className="kanban">

      {showAlert && (
        <div className="modal-overlay" onClick={resetAlertAndRestart}>
          <div className="modal" onClick={(e)=>e.stopPropagation()}>
            <h2>‚ö†Ô∏è Pausa Cognitiva</h2>
            <p>Voc√™ est√° h√° bastante tempo parado nesta a√ß√£o. Que tal respirar um pouco?</p>
            <button className="btn primary" onClick={resetAlertAndRestart}>Continuar</button>
          </div>
        </div>
      )}

      {confirmDelete && (
        <div className="modal-overlay" onClick={()=>setConfirmDelete(false)}>
          <div className="modal" onClick={(e)=>e.stopPropagation()}>
            <h2>Excluir tarefa?</h2>
            <p>Voc√™ tem certeza que quer excluir essa tarefa?</p>
            <div style={{display:'flex',gap:'12px',justifyContent:'center'}}>
              <button className="btn warn" onClick={deleteTask}>Sim</button>
              <button className="btn secondary" onClick={()=>setConfirmDelete(false)}>N√£o</button>
            </div>
          </div>
        </div>
      )}

      {confirmDone && (
        <div className="modal-overlay" onClick={()=>setConfirmDone(false)}>
          <div className="modal" onClick={(e)=>e.stopPropagation()}>
            <h2>Concluir tarefa?</h2>
            <p>Ap√≥s a tarefa ser conclu√≠da ela n√£o poder√° mais ser editada ou movida. Confirmar?</p>
            <div style={{display:'flex',gap:'12px',justifyContent:'center'}}>
              <button className="btn primary" onClick={confirmDoneMove}>Sim</button>
              <button className="btn secondary" onClick={()=>setConfirmDone(false)}>N√£o</button>
            </div>
          </div>
        </div>
      )}

      <div className="add-area">
        <input
          value={newTask}
          onChange={e=>{setNewTask(e.target.value); if(e.target.value.trim()) setInputError(false);}}
          placeholder={inputError ? "Nome da tarefa √© obrigat√≥rio" : "Digite uma tarefa..."}
          className={inputError ? "task-input error" : "task-input"}
        />
        <button className="btn add" onClick={addTask}>Adicionar</button>
      </div>

      {Object.entries(tasks).map(([key, list])=>{
        const typedKey = key as keyof Tasks;
        return(
          <div className="column" key={key}
            onDragOver={e=>e.preventDefault()}
            onDrop={e=>onDrop(e,typedKey)}
          >
            <h3>{key==='todo'?'A Fazer':key==='doing'?'Em Progresso':'Conclu√≠do'}</h3>

            {list.map((task, i)=>(
              <div
                className={`card ${key==='doing'?'pulse':''} ${key==='done'?'done':''}`}
                key={i}
                draggable={key!=='done'}
                onDragStart={e=>onDragStart(e,typedKey,i)}
              >
                <input
                  defaultValue={task.text}
                  className="task-inline-input"
                  disabled={key==='done'}
                  readOnly={key==='done'}
                />

                {key!=='done' && (
                  <button className="btn secondary" onClick={()=>addChecklist(typedKey,i)}>Adicionar checklist</button>
                )}

                <div className="progress-area">
                  {task.checklist.length>0 && (
                    <div className="progress-bar">
                      <div className="progress" style={{width: checklistProgress(task.checklist)+'%'}}></div>
                    </div>
                  )}
                </div>

                {showChecklistInput && showChecklistInput.column===typedKey && showChecklistInput.index===i && (
                  <input
                    className="task-input"
                    placeholder="Digite um passo..."
                    onKeyDown={(e:any)=>{
                      if(e.key==='Enter' && e.target.value.trim()){
                        saveChecklist(typedKey,i,e.target.value);
                      }
                    }}
                  />
                )}

                <ul className="checklist">
                  {task.checklist.map(c=>(
                    <li key={c.id}>
                      <label>
                        <input type="checkbox" disabled={key==='done'} checked={c.done} onChange={()=>toggleChecklist(typedKey,i,c.id)}/>
                        {c.text}
                      </label>
                    </li>
                  ))}
                </ul>

                <span className="trash" onClick={()=>confirmDeleteTask(typedKey,i)}>üóëÔ∏è</span>
              </div>
            ))}
          </div>
        );
      })}
    </section>
  );
}
