import { useEffect, useRef, useState } from 'react';

type Tasks = {
  todo: string[];
  doing: string[];
  done: string[];
};

export default function Kanban() {
  const [newTask, setNewTask] = useState('');
  const [showAlert, setShowAlert] = useState(false);
  const [tasks, setTasks] = useState<Tasks>({
    todo: [],
    doing: [],
    done: []
  });

  const inputRef = useRef<HTMLInputElement | null>(null);
  const timerRef = useRef<any>(null);

  function startInactivityTimer() {
    clearTimeout(timerRef.current);
    timerRef.current = setTimeout(() => {
      if (
        inputRef.current &&
        inputRef.current === document.activeElement &&
        inputRef.current.value.trim() === ''
      ) {
        setShowAlert(true);
      }
    }, 15000);
  }

  function resetAlertAndRestart() {
    setShowAlert(false);
    startInactivityTimer();
  }

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.addEventListener('focus', startInactivityTimer);
      inputRef.current.addEventListener('input', startInactivityTimer);
    }

    return () => {
      if (inputRef.current) {
        inputRef.current.removeEventListener('focus', startInactivityTimer);
        inputRef.current.removeEventListener('input', startInactivityTimer);
      }
      clearTimeout(timerRef.current);
    };
  }, []);

  function addTask() {
    if (!newTask.trim()) return;
    setTasks({
      ...tasks,
      todo: [...tasks.todo, newTask]
    });
    setNewTask('');
  }

  function onDragStart(
    e: React.DragEvent<HTMLDivElement>,
    column: keyof Tasks,
    index: number
  ) {
    e.dataTransfer.setData('source', JSON.stringify({ column, index }));
  }

  function onDrop(e: React.DragEvent<HTMLDivElement>, target: keyof Tasks) {
    const data = JSON.parse(e.dataTransfer.getData('source'));
    if (!data) return;

    const moved = tasks[data.column][data.index];

    const newData: Tasks = {
      todo: [...tasks.todo],
      doing: [...tasks.doing],
      done: [...tasks.done]
    };

    newData[data.column].splice(data.index, 1);
    newData[target].push(moved);

    setTasks(newData);
  }

  return (
    <section className="kanban">
      {showAlert && (
        <div className="modal-overlay" onClick={resetAlertAndRestart}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <h2>⚠️ Pausa Cognitiva</h2>
            <p>
              Você está há bastante tempo parado nesta ação. Que tal respirar
              um pouco?
            </p>
            <button className="btn primary" onClick={resetAlertAndRestart}>
              Continuar
            </button>
          </div>
        </div>
      )}

      <div className="add-area">
        <input
          ref={inputRef}
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Digite uma tarefa..."
          className="task-input"
        />
        <button className="btn add" onClick={addTask}>
          Adicionar
        </button>
      </div>

      {Object.entries(tasks).map(([key, list]) => {
        const typedKey = key as keyof Tasks;

        return (
          <div
            className="column"
            key={key}
            onDragOver={(e) => e.preventDefault()}
            onDrop={(e) => onDrop(e, typedKey)}
          >
            <h3>
              {key === 'todo'
                ? 'A Fazer'
                : key === 'doing'
                ? 'Em Progresso'
                : 'Concluído'}
            </h3>

            {list.map((item, i) => (
              <div
                className={`card ${
                  key === 'doing' ? 'pulse' : ''
                } ${key === 'done' ? 'done' : ''}`}
                key={i}
                draggable
                onDragStart={(e) => onDragStart(e, typedKey, i)}
                contentEditable
                suppressContentEditableWarning
              >
                {item}
              </div>
            ))}
          </div>
        );
      })}
    </section>
  );
}
